import Plugin from './Plugin';

/**
 * The plugin instance used when running ESDoc
 * @type {Plugin}
 */
export const plugin = new Plugin();

/**
 * Passes options to the plugin instance
 * @param {PluginEvent} e The event passed by ESDoc
 */
export function onStart(e) {
  plugin.handleOptions(e.data.option);
}

/**
 * Passes config to the plugin instance
 * @param {pluginEvent} e The event passed by ESDoc
 */
export function onHandleConfig(e) {
  plugin.handleConfig(e.data.config);
}

/**
 * Invokes {Plugin#checkCoverage}
 */
export function onComplete() {
  plugin.checkCoverage();
}

import expect from 'unexpected';
import Plugin from '../src/Plugin';

/** @test {Plugin} */
describe('Plugin', function() {
  let plugin;

  beforeEach(function() {
    plugin = new Plugin();
  });

  /** @test {Plugin#handleOptions} */
  describe('#handleOptions', function() {
    describe('option `required`', function() {
      it(`should default to ${Plugin.DefaultOptions.required}`, function() {
        plugin.handleOptions({});
        expect(plugin._required, 'to equal', Plugin.DefaultOptions.required);
      });

      it('should be configurable', function() {
        const required = 80;
        plugin.handleOptions({ required });
        expect(plugin._required, 'to equal', required);
      });
    });
  });

  /** @test {Plugin#handleConfig} */
  describe('#handleConfig', function() {
    describe('config `destination`', function() {
      it('should create coverageFilePath', function() {
        const destination = 'dest';
        plugin.handleConfig({ destination });
        expect(plugin._coverageFilePath, 'to begin with', destination);
      });
    });
  });

  /** @test {Plugin#checkCoverage} */
  describe('#checkCoverage', function() {
    function runWithOptionsConfig(options, config) {
      plugin.handleOptions(options);
      plugin.handleConfig(config);
      plugin.checkCoverage();
    }

    it('should throw an error if coverage.json does not exist', function() {
      expect(() => {
        runWithOptionsConfig({}, { destination: 'destination' });
      }, 'to throw', /coverage\.json file not found/);
    });

    it('should throw an error if coverage is to low', function() {
      expect(() => {
        runWithOptionsConfig({}, { destination: 'test/fixtures/not-documented/out' });
      }, 'to throw', /Coverage is at ([0-9])+%/);
    });

    it('should pass if coverage is high enough', function() {
      expect(() => {
        runWithOptionsConfig({}, { destination: 'test/fixtures/documented/out' });
      }, 'not to throw');
    });
  });
});

import { spawn } from 'child_process';
import expect from 'unexpected';
import { stub } from 'sinon';
import { PluginEvent } from 'esdoc/out/src/Plugin/Plugin';
import * as requireCoverage from '../src/require-coverage';

describe('require-coverage', function() {
  /** @test {onStart} */
  it('should export onStart', function() {
    expect(requireCoverage.onStart, 'to be defined');
  });

  /** @test {onHandleConfig} */
  it('should export onHandleConfig', function() {
    expect(requireCoverage.onHandleConfig, 'to be defined');
  });

  /** @test {onComplete} */
  it('should export onComplete', function() {
    expect(requireCoverage.onComplete, 'to be defined');
  });

  /** @test {onStart} */
  describe('#onStart', function() {
    it('should call Plugin#handleOptions', function() {
      const handleOptions = stub(requireCoverage.plugin, 'handleOptions');
      const option = { test: true };

      requireCoverage.onStart(new PluginEvent({ option }));
      expect(handleOptions.calledOnce, 'to be true');
      expect(handleOptions.calledWith(option), 'to be true');
    });
  });

  /** @test {onStart} */
  describe('#onStart', function() {
    it('should call Plugin#handleOptions', function() {
      const handleConfig = stub(requireCoverage.plugin, 'handleConfig');
      const config = { destination: 'dest' };

      requireCoverage.onHandleConfig(new PluginEvent({ config }));
      expect(handleConfig.calledOnce, 'to be true');
      expect(handleConfig.calledWith(config), 'to be true');
    });
  });

  /** @test {onComplete} */
  describe('#onComplete', function() {
    it('should call Plugin#checkCoverage', function() {
      const checkCoverage = stub(requireCoverage.plugin, 'checkCoverage');

      requireCoverage.onComplete(new PluginEvent({}));
      expect(checkCoverage.calledOnce, 'to be true');
    });

    this.timeout(10000);

    context('with undocumented project', function() {
      it('should should exit with code 1', function(done) {
        const child = spawn('./node_modules/.bin/esdoc', [
          '-c',
          './test/fixtures/not-documented/not-documented.json',
        ]);

        child.on('close', function(code) {
          expect(code, 'to equal', 1);
          done();
        });
      });
    });

    context('with documented project', function() {
      it('should should exit with code 0', function(done) {
        const child = spawn('./node_modules/.bin/esdoc', [
          '-c',
          './test/fixtures/documented/documented.json',
        ]);

        child.on('close', function(code) {
          expect(code, 'to equal', 0);
          done();
        });
      });
    });
  });
});
